import './App.css';
import React, { useState, useEffect} from 'react';
import axios from 'axios';
import AddBookForm from './AddBookForm';
import BookList from './BookList';

function App() {

  const myAPI = "https://65427340ad8044116ed3651e.mockapi.io/booklist/";

  const [books,setBooks] = useState([ ]);

  useEffect( ()=>{
    axios.get(myAPI).then(response =>{
      const data = (response.data);
      setBooks(data);
    }
    )
  },[]);

  function handleDelete(id){
    //console.log(myAPI+`${id}`);
    axios.delete(myAPI+`${id}`).then(response => console.log(response));
    axios.get(myAPI).then(response=>console.log(response.data));

    // const newBooks = books.filter(book=> book.id !== id);
    // console.log(newBooks);
    // setBooks(newBooks);

  }

  return (
    <>
    <h1 id="title">Favorite Books</h1>
    <AddBookForm/>
    <BookList books={books} Delete={handleDelete}/>
    </>
  );
}

export default App;
