    console.log(e.target.value);
    setMessge(e.target.value)

  };

  ///// Add book to our MockAPI and display it on screen //////


  return (
    <>
    <h1 id="title">Favorite Books</h1>
    <AddBookForm handleInput= {handleInput}/>
    <BookList books={books} Delete={handleDelete}/>
    </>
  );
}

export default App;
